function beforeAll(graphModel, graphSelection) {
  let html = '<div ng-repeat="(type,enabled) in types">'+
    '<input type="checkbox" ng-model="types[type]"> {{type}} </input>'+
    '</div>';
  f.openModal('Choose the type of items to be selected', html);

  return {
    model: null,
    selection: null
  };
}

function onModalShow(scope, graphModel) {
  let graphTypesSet = new Set();
  _.each(graphModel.nodes, function (node) {
    graphTypesSet.add(node.type);
  });
  scope.types = {};

  _.each(Array.from(graphTypesSet), function (type) {
    scope.types[type] = false;
  });
}

function onModalOk(scope, graphModel) {
  let selection = [];

  _.each(graphModel.nodes, function (node) {
    if (scope.types[node.type]) {
      selection.push(node.id);
    }
  });

  return {
    model: null,
    selection: selection
  };
}

function afterModalClosed(graphModel, graphSelection, onOkModalResult) {
  return {
    model: null,
    selection: onOkModalResult.selection
  };
}
