function beforeAll(graphModel, graphSelection) {
  let newSelection = [];

  _.each(graphSelection, function (selectedNode) {
    _.each(graphModel.relations, function (relation) {
      if (relation.in === selectedNode) {
        newSelection.push(relation.out);
      } 
        if (relation.out === selectedNode) {
        newSelection.push(relation.in);
      }
    });
    newSelection.push(selectedNode);
  });

  return { selection: newSelection};
}
