function beforeAll(graphModel, graphSelection) {
  let oldSelectionSet = new Set(graphSelection);
  let newSelection = [];

  _.each(graphModel.nodes, function (node) {
    if (!oldSelectionSet.has(node.id)) {
      newSelection.push(node.id);
    }
  });

  return Promise.resolve({
    model: null,
    selection: newSelection
  });
}
