function beforeAll(graphModel, graphSelection) {
    if (graphSelection.length === 2) {
        let dd = '<select ng-options="i as i for i in [2,3,4]" ng-model="maxSize">';
        dd = dd + '<option style="display:none" value="">select a number</option>';
        dd = dd + '</select>';

        _openModal('Pick the max path length', dd);
    } else {
        alert('Warning: This script only works with 2 selected nodes');
    }
    return null;
}

function onModalOk(scope, graphModel) {
    return scope.maxSize;
}

function afterModalClosed(graphModel, graphSelection, onOkModalResult) {
    i = 1;
    return iterateQuery(i, graphSelection, onOkModalResult);
}

function iterateQuery(i, graphSelection, maxIteration) {
    if (i <= maxIteration) {
        let query = 'g.V("' + graphSelection[0] + 
        '").repeat(both().simplePath()).times(' + i + ').path()';

        return _runGremlinQuery(query).then(function (results) {
            let shortestPath;
            for (let i=0; i<results.length; i++) {
                let path = results[i];
                    if (path.objects[path.objects.length-1].id === graphSelection[1]) {
                        shortestPath = path;
                        break;
                    }
                if (shortestPath) {
                    break;
                }
            }

            if (shortestPath) {
                _addResultsToGraph(shortestPath);
            }  else {
                i++;
                return iterateQuery(i, graphSelection, maxIteration);
            }
        });
    }
}
