function beforeAll(graphModel, graphSelection) {
  let typeCounter = {};

  _.each(graphModel.nodes, function (node) {
    if (typeCounter[node.type]) {
      typeCounter[node.type] = typeCounter[node.type] + 1;
    } else {
      typeCounter[node.type] = 1;
    }
  });

  let prettyString = '<ul>';
  for (var key in typeCounter) {
    if (typeCounter.hasOwnProperty(key)) {
      prettyString = prettyString + '<li>';
      prettyString = prettyString + key + ': ' + typeCounter[key];
      prettyString = prettyString + '</li>';
    }
  }
  prettyString = prettyString + '</ul>';

  f.openModal('Number of nodes in the current graph', prettyString);
  return Promise.resolve(null);
}
