{{! Handlebars generic table template example}}

{{#if config.templateVars.hidden}}
{{else}}
  {{!-- generic template to render sparql select query results (raw response object) --}}
  {{#if config.open}}
    <span class="snippetLabel">{{config.templateVars.label}}</span>
    <span> ({{results.bindings.length}})</span>
    <a href="javascript:showHide('{{id}}');" id="button-{{id}}" class="snippetShowHideButton" style="font-size:12px;">(hide)</a> 
    <div id="cont-{{id}}"  style="display: block;">
  {{else}}
    <span class="snippetLabel">{{config.templateVars.label}}</span>
    <span> ({{results.bindings.length}})</span>
    <a href="javascript:showHide('{{id}}');" id="button-{{id}}" class="snippetShowHideButton" style="font-size:12px;">(show)</a> 
    <div id="cont-{{id}}"  style="display: none;">
  {{/if}}
  <style>
    table.custom-header th {color:black;}
  </style>
  <table class="table table-condensed custom-header">
      <thead>
      <tr>
          {{#each head.vars}}
              <th>{{this}}</th>
          {{/each}}
      </tr>
      </thead>
      <tbody>
      {{#each results.bindings}}
      <tr>
          {{#each ../head.vars}}

            {{!-- 
                  // because of this there is a custome helper  
                  here ../this - refers to single binding 
                  while this   - refers to variable name
            --}}

            {{#getVariableValue ../this this "uri" }}
              <td>
                <a href="{{value}}">{{label}}</a>
              </td>
            {{else}}
              <td>
                {{value}}
              </td>
            {{/getVariableValue}}
          {{/each}}
      </tr>
      {{/each}}
      </tbody>
  </table>
  <br/>
  <br/>
  <script>
    // here it is how to pass the results  
    var x = {{{json results}}};
    // console.log(x);

   function showHide(id){
    var buttonEl = document.getElementById('button-' + id);
    var contEl = document.getElementById('cont-' + id);
    var style = window.getComputedStyle(contEl);

    if (style.display == 'none') {
      contEl.style.display = 'block';
      buttonEl.innerHTML = 'hide';
    } else {
      buttonEl.innerHTML = 'show';
      contEl.style.display = 'none';
    }
  }
<{{!}}/script>
  </div>
{{/if}}
